(8, 1, 'Customer Order Processing - Complete Workflow', 'End-to-end customer order process involving CS, Sales, Accounting, and Customer touchpoints. Includes inquiry handling, quote preparation, credit checks, order fulfillment, and payment processing.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<bpmn2:definitions xmlns:bpmn2=\"http://www.omg.org/spec/BPMN/20100524/MODEL\" xmlns:bpmndi=\"http://www.omg.org/spec/BPMN/20100524/DI\" xmlns:dc=\"http://www.omg.org/spec/DD/20100524/DC\" xmlns:di=\"http://www.omg.org/spec/DD/20100524/DI\" id=\"customer-order-process\" targetNamespace=\"http://macta.htt.com\">\n  <bpmn2:process id=\"CustomerOrderProcess\" name=\"Customer Order Processing\" isExecutable=\"false\">\n    <bpmn2:startEvent id=\"StartEvent_CustomerInquiry\" name=\"Customer Submits Inquiry\">\n      <bpmn2:outgoing>Flow_1</bpmn2:outgoing>\n    </bpmn2:startEvent>\n    <bpmn2:userTask id=\"Task_CS_ReviewInquiry\" name=\"CS: Review &#38; Validate Inquiry\">\n      <bpmn2:incoming>Flow_1</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_2</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Sales_PrepareQuote\" name=\"Sales: Prepare Quote &#38; Proposal\">\n      <bpmn2:incoming>Flow_2</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_3</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Customer_ReviewQuote\" name=\"Customer: Review &#38; Approve Quote\">\n      <bpmn2:incoming>Flow_3</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_4</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Sales_CreateOrder\" name=\"Sales: Create Sales Order\">\n      <bpmn2:incoming>Flow_4</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_5</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Accounting_CreditCheck\" name=\"Accounting: Credit Check &#38; Approval\">\n      <bpmn2:incoming>Flow_5</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_6</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_CS_OrderConfirmation\" name=\"CS: Send Order Confirmation\">\n      <bpmn2:incoming>Flow_6</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_7</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_CS_ScheduleDelivery\" name=\"CS: Schedule Delivery/Implementation\">\n      <bpmn2:incoming>Flow_7</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_8</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Customer_ReceiveService\" name=\"Customer: Receive Product/Service\">\n      <bpmn2:incoming>Flow_8</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_9</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Accounting_GenerateInvoice\" name=\"Accounting: Generate Invoice\">\n      <bpmn2:incoming>Flow_9</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_10</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Customer_Payment\" name=\"Customer: Process Payment\">\n      <bpmn2:incoming>Flow_10</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_11</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:userTask id=\"Task_Accounting_Reconciliation\" name=\"Accounting: Payment Reconciliation\">\n      <bpmn2:incoming>Flow_11</bpmn2:incoming>\n      <bpmn2:outgoing>Flow_12</bpmn2:outgoing>\n    </bpmn2:userTask>\n    <bpmn2:endEvent id=\"EndEvent_OrderComplete\" name=\"Order Complete\">\n      <bpmn2:incoming>Flow_12</bpmn2:incoming>\n    </bpmn2:endEvent>\n    <bpmn2:sequenceFlow id=\"Flow_1\" sourceRef=\"StartEvent_CustomerInquiry\" targetRef=\"Task_CS_ReviewInquiry\" />\n    <bpmn2:sequenceFlow id=\"Flow_2\" sourceRef=\"Task_CS_ReviewInquiry\" targetRef=\"Task_Sales_PrepareQuote\" />\n    <bpmn2:sequenceFlow id=\"Flow_3\" sourceRef=\"Task_Sales_PrepareQuote\" targetRef=\"Task_Customer_ReviewQuote\" />\n    <bpmn2:sequenceFlow id=\"Flow_4\" sourceRef=\"Task_Customer_ReviewQuote\" targetRef=\"Task_Sales_CreateOrder\" />\n    <bpmn2:sequenceFlow id=\"Flow_5\" sourceRef=\"Task_Sales_CreateOrder\" targetRef=\"Task_Accounting_CreditCheck\" />\n    <bpmn2:sequenceFlow id=\"Flow_6\" sourceRef=\"Task_Accounting_CreditCheck\" targetRef=\"Task_CS_OrderConfirmation\" />\n    <bpmn2:sequenceFlow id=\"Flow_7\" sourceRef=\"Task_CS_OrderConfirmation\" targetRef=\"Task_CS_ScheduleDelivery\" />\n    <bpmn2:sequenceFlow id=\"Flow_8\" sourceRef=\"Task_CS_ScheduleDelivery\" targetRef=\"Task_Customer_ReceiveService\" />\n    <bpmn2:sequenceFlow id=\"Flow_9\" sourceRef=\"Task_Customer_ReceiveService\" targetRef=\"Task_Accounting_GenerateInvoice\" />\n    <bpmn2:sequenceFlow id=\"Flow_10\" sourceRef=\"Task_Accounting_GenerateInvoice\" targetRef=\"Task_Customer_Payment\" />\n    <bpmn2:sequenceFlow id=\"Flow_11\" sourceRef=\"Task_Customer_Payment\" targetRef=\"Task_Accounting_Reconciliation\" />\n    <bpmn2:sequenceFlow id=\"Flow_12\" sourceRef=\"Task_Accounting_Reconciliation\" targetRef=\"EndEvent_OrderComplete\" />\n  </bpmn2:process>\n  <bpmndi:BPMNDiagram id=\"BPMNDiagram_1\">\n    <bpmndi:BPMNPlane id=\"BPMNPlane_1\" bpmnElement=\"CustomerOrderProcess\">\n      <bpmndi:BPMNShape id=\"StartEvent_CustomerInquiry_di\" bpmnElement=\"StartEvent_CustomerInquiry\">\n        <dc:Bounds x=\"152\" y=\"102\" width=\"36\" height=\"36\" />\n        <bpmndi:BPMNLabel>\n          <dc:Bounds x=\"130\" y=\"145\" width=\"80\" height=\"27\" />\n        </bpmndi:BPMNLabel>\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_CS_ReviewInquiry_di\" bpmnElement=\"Task_CS_ReviewInquiry\">\n        <dc:Bounds x=\"240\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Sales_PrepareQuote_di\" bpmnElement=\"Task_Sales_PrepareQuote\">\n        <dc:Bounds x=\"390\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Customer_ReviewQuote_di\" bpmnElement=\"Task_Customer_ReviewQuote\">\n        <dc:Bounds x=\"540\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Sales_CreateOrder_di\" bpmnElement=\"Task_Sales_CreateOrder\">\n        <dc:Bounds x=\"690\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Accounting_CreditCheck_di\" bpmnElement=\"Task_Accounting_CreditCheck\">\n        <dc:Bounds x=\"840\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_CS_OrderConfirmation_di\" bpmnElement=\"Task_CS_OrderConfirmation\">\n        <dc:Bounds x=\"990\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_CS_ScheduleDelivery_di\" bpmnElement=\"Task_CS_ScheduleDelivery\">\n        <dc:Bounds x=\"1140\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Customer_ReceiveService_di\" bpmnElement=\"Task_Customer_ReceiveService\">\n        <dc:Bounds x=\"1290\" y=\"80\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Accounting_GenerateInvoice_di\" bpmnElement=\"Task_Accounting_GenerateInvoice\">\n        <dc:Bounds x=\"240\" y=\"200\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Customer_Payment_di\" bpmnElement=\"Task_Customer_Payment\">\n        <dc:Bounds x=\"390\" y=\"200\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"Task_Accounting_Reconciliation_di\" bpmnElement=\"Task_Accounting_Reconciliation\">\n        <dc:Bounds x=\"540\" y=\"200\" width=\"100\" height=\"80\" />\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNShape id=\"EndEvent_OrderComplete_di\" bpmnElement=\"EndEvent_OrderComplete\">\n        <dc:Bounds x=\"692\" y=\"222\" width=\"36\" height=\"36\" />\n        <bpmndi:BPMNLabel>\n          <dc:Bounds x=\"672\" y=\"265\" width=\"76\" height=\"14\" />\n        </bpmndi:BPMNLabel>\n      </bpmndi:BPMNShape>\n      <bpmndi:BPMNEdge id=\"Flow_1_di\" bpmnElement=\"Flow_1\">\n        <di:waypoint x=\"188\" y=\"120\" />\n        <di:waypoint x=\"240\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_2_di\" bpmnElement=\"Flow_2\">\n        <di:waypoint x=\"340\" y=\"120\" />\n        <di:waypoint x=\"390\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_3_di\" bpmnElement=\"Flow_3\">\n        <di:waypoint x=\"490\" y=\"120\" />\n        <di:waypoint x=\"540\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_4_di\" bpmnElement=\"Flow_4\">\n        <di:waypoint x=\"640\" y=\"120\" />\n        <di:waypoint x=\"690\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_5_di\" bpmnElement=\"Flow_5\">\n        <di:waypoint x=\"790\" y=\"120\" />\n        <di:waypoint x=\"840\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_6_di\" bpmnElement=\"Flow_6\">\n        <di:waypoint x=\"940\" y=\"120\" />\n        <di:waypoint x=\"990\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_7_di\" bpmnElement=\"Flow_7\">\n        <di:waypoint x=\"1090\" y=\"120\" />\n        <di:waypoint x=\"1140\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_8_di\" bpmnElement=\"Flow_8\">\n        <di:waypoint x=\"1240\" y=\"120\" />\n        <di:waypoint x=\"1290\" y=\"120\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_9_di\" bpmnElement=\"Flow_9\">\n        <di:waypoint x=\"1340\" y=\"160\" />\n        <di:waypoint x=\"1340\" y=\"340\" />\n        <di:waypoint x=\"290\" y=\"340\" />\n        <di:waypoint x=\"290\" y=\"280\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_10_di\" bpmnElement=\"Flow_10\">\n        <di:waypoint x=\"340\" y=\"240\" />\n        <di:waypoint x=\"390\" y=\"240\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_11_di\" bpmnElement=\"Flow_11\">\n        <di:waypoint x=\"490\" y=\"240\" />\n        <di:waypoint x=\"540\" y=\"240\" />\n      </bpmndi:BPMNEdge>\n      <bpmndi:BPMNEdge id=\"Flow_12_di\" bpmnElement=\"Flow_12\">\n        <di:waypoint x=\"640\" y=\"240\" />\n        <di:waypoint x=\"692\" y=\"240\" />\n      </bpmndi:BPMNEdge>\n    </bpmndi:BPMNPlane>\n  </bpmndi:BPMNDiagram>\n</bpmn2:definitions>\n', '2025-08-16 11:40:07', '2025-08-16 11:47:30');
