CREATE DEFINER=`root`@`localhost` PROCEDURE `CalculateProcessCost` (IN `process_id` INT, OUT `total_cost` DECIMAL(10,2))   BEGIN
    SELECT COALESCE(SUM(er.hourly_cost * (tra.duration_minutes / 60.0) * tra.quantity_required), 0)
    INTO total_cost
    FROM task_resource_assignments tra
    JOIN enhanced_resources er ON tra.resource_id = er.id
    WHERE tra.process_id = process_id;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `GetOptimalResourceAllocation` (IN `process_id` INT)   BEGIN
    SELECT 
        tra.task_id,
        er.name as resource_name,
        er.type,
        er.skill_level,
        tra.duration_minutes,
        tra.complexity_level,
        (er.hourly_cost * (tra.duration_minutes / 60.0) * tra.quantity_required) as task_cost,
        (er.efficiency_factor * 100) as efficiency_percentage,
        CASE 
            WHEN er.skill_level = 'expert' AND tra.complexity_level IN ('complex', 'critical') THEN 'Optimal'
            WHEN er.skill_level = 'advanced' AND tra.complexity_level IN ('moderate', 'complex') THEN 'Good'
            WHEN er.skill_level = 'intermediate' AND tra.complexity_level IN ('simple', 'moderate') THEN 'Adequate'
            ELSE 'Sub-optimal'
        END as skill_match_rating
    FROM task_resource_assignments tra
    JOIN enhanced_resources er ON tra.resource_id = er.id
    WHERE tra.process_id = process_id
    ORDER BY 
        CASE skill_match_rating
            WHEN 'Optimal' THEN 1
            WHEN 'Good' THEN 2
            WHEN 'Adequate' THEN 3
            ELSE 4
        END,
        task_cost ASC;
END$$

