    DECLARE time_factor DECIMAL(5,2) DEFAULT 0.0;
    DECLARE cost_factor DECIMAL(5,2) DEFAULT 0.0;
    DECLARE resource_factor DECIMAL(5,2) DEFAULT 0.0;
    
    -- Calculate time efficiency (lower duration = higher score)
    SELECT COALESCE(100 - (total_duration / 10), 50) INTO time_factor
    FROM process_path_analysis 
    WHERE process_id = process_id AND analysis_type = 'critical'
    LIMIT 1;
    
    -- Calculate cost efficiency (lower cost = higher score)
    SELECT COALESCE(100 - (total_cost / 10), 50) INTO cost_factor
    FROM process_path_analysis 
    WHERE process_id = process_id AND analysis_type = 'critical'
    LIMIT 1;
    
    -- Calculate resource efficiency (optimal resource usage)
    SELECT COALESCE(AVG(efficiency_factor * 20), 50) INTO resource_factor
    FROM enhanced_resources er
    JOIN task_resource_assignments tra ON er.id = tra.resource_id
    WHERE tra.process_id = process_id;
    
    -- Weighted average (time: 40%, cost: 35%, resources: 25%)
    SET efficiency_score = (time_factor * 0.4) + (cost_factor * 0.35) + (resource_factor * 0.25);
    
    RETURN GREATEST(0, LEAST(100, efficiency_score));
END$$

