--
-- Table structure for table `process_resources`
--

CREATE TABLE `process_resources` (
  `id` int NOT NULL,
  `process_id` int NOT NULL,
  `task_id` varchar(100) COLLATE utf8mb4_general_ci NOT NULL,
  `resource_id` int NOT NULL,
  `quantity_required` decimal(10,2) DEFAULT '1.00',
  `duration_minutes` int DEFAULT '60',
  `complexity_level` enum('low','medium','high') COLLATE utf8mb4_general_ci DEFAULT 'medium',
  `priority_level` enum('low','medium','high','critical') COLLATE utf8mb4_general_ci DEFAULT 'medium',
  `assigned_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `status` enum('active','inactive') COLLATE utf8mb4_general_ci DEFAULT 'active'
